ss=\"wp-block-image size-full is-resized is-style-default\"><img src=\"https://greta.lopia.fr/wp-content/uploads/2023/02/Partner-Delta-Dore.jpg\" alt=\"\" class=\"wp-image-292\" width=\"132\" height=\"83\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column -->\n\n<!-- wp:column -->\n<div class=\"wp-block-column\"></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:image {\"id\":293,\"width\":1365,\"height\":439,\"sizeSlug\":\"large\",\"linkDestination\":\"none\"} -->\n<figure class=\"wp-block-image size-large is-resized\"><img src=\"https://greta.lopia.fr/wp-content/uploads/2023/02/Partner-UE-occitanie-1024x330.jpg\" alt=\"\" class=\"wp-image-293\" width=\"1365\" height=\"439\"/></figure>\n<!-- /wp:image --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->', 'Accueil', '', 'inherit', 'closed', 'closed', '', '29-revision-v1', '', '', '2023-02-10 19:55:42', '2023-02-10 18:55:42', '', 29, 'https://greta.lopia.fr/?p=682', 0, 'revision', '', 0);
