item --></ul>\n<!-- /wp:list -->\n\n<!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column {\"width\":\"100%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:100%\"><!-- wp:group {\"layout\":{\"type\":\"constrained\"}} -->\n<div class=\"wp-block-group\"><!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column {\"width\":\"100%\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:100%\"><!-- wp:group {\"layout\":{\"type\":\"flex\",\"flexWrap\":\"nowrap\"}} -->\n<div class=\"wp-block-group\"><!-- wp:columns -->\n<div class=\"wp-block-columns\"><!-- wp:column -->\n<div class=\"wp-block-column\"><!-- wp:paragraph -->\n<p><strong>Contenus transversaux :</strong></p>\n<!-- /wp:paragraph --></div>\n<!-- /wp:column -->\n\n<!-- wp:column {\"width\":\"400px\"} -->\n<div class=\"wp-block-column\" style=\"flex-basis:400px\"><!-- wp:list -->\n<ul><!-- wp:list-item -->\n<li>Anglais appliqué au domaine informatique</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Techniques de recueil, synthèse et rédaction</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Maîtrise des outils nécessaires à un travail agile et collaboratif</li>\n<!-- /wp:list-item -->\n\n<!-- wp:list-item -->\n<li>Techniques de recherche d\'emploi</li>\n<!-- /wp:list-item --></ul>\n<!-- /wp:list --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:group --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns --></div>\n<!-- /wp:group --></div>\n<!-- /wp:column --></div>\n<!-- /wp:columns -->\n\n<!-- wp:heading {\"textAlign\":\"left\",\"style\":{\"typography\":{\"textTransform\":\"uppercase\"}},\"textColor\":\"luminous-vivid-orange\"} -->\n<h2 class=\"has-text-align-left has-luminous-vivid-orange-color has-text-color\" id=\"finance\" style=\"text-transform:uppercase\">intervenants</h2>\n<!-- /wp:heading -->\n\n<!-- wp:paragraph {\"align\":\"left\"} -->\n<p class=\"has-text-align-left\">Formateurs experts du domaine</p>\n<!-- /wp:paragraph 